import React, { useState } from 'react';
import EditTask from '../Modals/EditTask';

const Card = ({ taskObj, index, deleteTask, updateListArray }) => {
  const [modal, setModal] = useState(false);

  const colors = [
    {
      primaryColor: '#5D93E1',
      secondaryColor: '#ECF3FC',
    },
    {
      primaryColor: '#F9D288',
      secondaryColor: '#FEFAF1',
    },
    {
      primaryColor: '#5DC250',
      secondaryColor: '#F2FAF1',
    },
    {
      primaryColor: '#F48687',
      secondaryColor: '#FDF1F1',
    },
    {
      primaryColor: '#B964F7',
      secondaryColor: '#F3F0FD',
    },
  ];

  const toggle = () => {
    setModal(!modal);
  };

  const updateTask = (obj) => {
    updateListArray(obj, index);
    setModal(false);
  };

  const handleDelete = () => {
    deleteTask(index);
  };

  return (
    <div className="card-wrapper mr-5" style={{ marginLeft: '20px' }}>
      <div className="card-top" style={{ backgroundColor: colors[index % 5].primaryColor }}></div>
      <div className="task-holder">
        <span className="card-header" style={{ backgroundColor: colors[index % 5].secondaryColor, borderRadius: '10px' }}>
          {taskObj.Name}
        </span>
        <p className="mt-3">{taskObj.Description}</p>
        <p>Date: {taskObj.Date}</p>

        <div className="card-icons">
          <div className="card-icons-left">
            <i
              className={`far ${taskObj.IsCompleted ? 'fa-check-circle' : 'fa-circle'}`}
              style={{ color: colors[index % 5].primaryColor, marginRight: '5px' }}
            ></i>
            <i
              className={`fas ${taskObj.IsImportant ? 'fa-star' : 'fa-star-half-alt'}`}
              style={{ color: colors[index % 5].primaryColor, marginRight: '5px' }}
            ></i>
          </div>
          <div className="card-icons-right">
            <i
              className="far fa-edit"
              style={{ color: colors[index % 5].primaryColor, marginRight: '5px', cursor: 'pointer' }}
              onClick={() => setModal(true)}
            ></i>
            <i
              className="fas fa-trash-alt"
              style={{ color: colors[index % 5].primaryColor, cursor: 'pointer' }}
              onClick={handleDelete}
            ></i>
          </div>
        </div>
      </div>
      <EditTask modal={modal} toggle={toggle} updateTask={updateTask} taskObj={taskObj} />
    </div>
  );
};

export default Card;
